contracts/FixedPriceOracle.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/IPriceOracle.sol";
import "./libs/StringUtils.sol";
import "./libs/Ownable.sol";
import "./libs/IERC165.sol";

// FixedPriceOracle sets a price in FIL
contract FixedPriceOracle is IPriceOracle {
    using StringUtils for *;

    // Rent in base price units by length
    uint256 public immutable price1Letter;
    uint256 public immutable price2Letter;
    uint256 public immutable price3Letter;
    uint256 public immutable price4Letter;
    uint256 public immutable price5Letter;

    constructor() {
        // price1Letter = _rentPrices[0];
        // price2Letter = _rentPrices[1];
        // price3Letter = _rentPrices[2];
        // price4Letter = _rentPrices[3];
        // price5Letter = _rentPrices[4];
        price1Letter = 3 * 1e15;
        price2Letter = 3 * 1e14;
        price3Letter = 3 * 1e13;
        price4Letter = 3 * 1e12;
        price5Letter = 3 * 1e11;
    }

    function price(
        string calldata name,
        uint256 expires,
        uint256 duration
    ) external view override returns (IPriceOracle.Price memory) {
        uint256 len = name.strlen();
        uint256 basePrice;

        if (len >= 5) {
            basePrice = price5Letter * duration;
        } else if (len == 4) {
            basePrice = price4Letter * duration;
        } else if (len == 3) {
            basePrice = price3Letter * duration;
        } else if (len == 2) {
            basePrice = price2Letter * duration;
        } else {
            basePrice = price1Letter * duration;
        }

        return IPriceOracle.Price({ base: basePrice, premium: _premium(name, expires, duration) });
    }

    /**
     * @dev Returns the pricing premium in wei.
     */
    function premium(
        string calldata name,
        uint256 expires,
        uint256 duration
    ) external view returns (uint256) {
        return _premium(name, expires, duration);
    }

    /**
     * @dev Returns the pricing premium in internal base units.
     */
    function _premium(
        string memory, /*name*/
        uint256, /*expires*/
        uint256 /*duration*/
    ) internal view virtual returns (uint256) {
        return 0;
    }

    function supportsInterface(bytes4 interfaceID) public view virtual returns (bool) {
        return interfaceID == type(IERC165).interfaceId || interfaceID == type(IPriceOracle).interfaceId;
    }
}

contracts/FNSDeployer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./FixedPriceOracle.sol";
import "./Registrar.sol";
import "./RegistrarController.sol";
import "./FNSRegistry.sol";
import "./PublicResolver.sol";
import "./ReverseRegistrar.sol";

// not finished yet
contract FNSDeployer {
    FNSRegistry public immutable registry;
    ReverseRegistrar public immutable reverseRegistrar;
    FixedPriceOracle public immutable priceOracle;
    Registrar public immutable registrar;
    RegistrarController public immutable registrarController;
    PublicResolver public immutable resolver;

    bytes32 private constant ROOT = bytes32(0);
    bytes32 private constant FIL_LABLE = keccak256("fil");
    bytes32 private constant REVERSE_LABLE = keccak256("reverse");
    bytes32 private constant ADDR_LABLE = keccak256("addr");
    string private constant RESOLVER_NAME = "resolver";
    uint256 public constant RESERVE_DURATION = 1e10 days;

    constructor() {
        address owner = msg.sender;
        registry = new FNSRegistry();

        // reverse
        reverseRegistrar = new ReverseRegistrar(registry);
        registry.setSubnodeOwner(ROOT, REVERSE_LABLE, address(this));
        registry.setSubnodeOwner(_makeNode(ROOT, REVERSE_LABLE), ADDR_LABLE, address(reverseRegistrar));

        // registrar
        priceOracle = new FixedPriceOracle();
        registrar = new Registrar(registry, _makeNode(ROOT, FIL_LABLE));
        registry.setSubnodeOwner(ROOT, FIL_LABLE, address(registrar));
        registrar.addController(address(this));
        registrarController = new RegistrarController(registrar, priceOracle, reverseRegistrar);
        registrar.addController(address(registrarController));
        reverseRegistrar.setController(address(registrarController), true);

        // resolver
        resolver = new PublicResolver(registry, address(registrarController), address(reverseRegistrar));
        registrar.register(RESOLVER_NAME, owner, RESERVE_DURATION, address(resolver));
        reverseRegistrar.setDefaultResolver(address(resolver));
        registrar.setResolver(address(resolver));
        // todo: resolver.setAddr();

        registry.setOwner(ROOT, owner);
        reverseRegistrar.transferOwnership(owner);
        registrar.removeController(address(this));
        registrar.transferOwnership(owner);
        registrarController.transferOwnership(owner);
    }

    function _makeNode(bytes32 node, bytes32 labelhash) private pure returns (bytes32) {
        return keccak256(abi.encodePacked(node, labelhash));
    }
}

contracts/FNSRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/FNS.sol";

/**
 * The FNS registry contract.
 */
contract FNSRegistry is FNS {
    struct Record {
        address owner;
        address resolver;
        uint64 ttl;
    }

    mapping(bytes32 => Record) public records;
    mapping(address => mapping(address => bool)) operators;

    // Permits modifications only by the owner of the specified node.
    modifier authorised(bytes32 node) {
        address rOwner = records[node].owner;
        require(rOwner == msg.sender || operators[rOwner][msg.sender]);
        _;
    }

    /**
     * @dev Constructs a new FNS registry.
     */
    constructor() {
        records[0x0].owner = msg.sender;
    }

    /**
     * @dev Sets the record for a node.
     * @param node The node to update.
     * @param _owner The address of the new owner.
     * @param _resolver The address of the resolver.
     * @param _ttl The TTL in seconds.
     */
    function setRecord(
        bytes32 node,
        address _owner,
        address _resolver,
        uint64 _ttl
    ) external virtual override {
        setOwner(node, _owner);
        _setResolverAndTTL(node, _resolver, _ttl);
    }

    /**
     * @dev Sets the record for a subnode.
     * @param node The parent node.
     * @param label The hash of the label specifying the subnode.
     * @param _owner The address of the new owner.
     * @param _resolver The address of the resolver.
     * @param _ttl The TTL in seconds.
     */
    function setSubnodeRecord(
        bytes32 node,
        bytes32 label,
        address _owner,
        address _resolver,
        uint64 _ttl
    ) external virtual override {
        bytes32 subnode = setSubnodeOwner(node, label, _owner);
        _setResolverAndTTL(subnode, _resolver, _ttl);
    }

    /**
     * @dev Transfers ownership of a node to a new address. May only be called by the current owner of the node.
     * @param node The node to transfer ownership of.
     * @param _owner The address of the new owner.
     */
    function setOwner(bytes32 node, address _owner) public virtual override authorised(node) {
        _setOwner(node, _owner);
        emit Transfer(node, _owner);
    }

    /**
     * @dev Transfers ownership of a subnode keccak256(node, label) to a new address. May only be called by the owner of the parent node.
     * @param node The parent node.
     * @param label The hash of the label specifying the subnode.
     * @param _owner The address of the new owner.
     */
    function setSubnodeOwner(
        bytes32 node,
        bytes32 label,
        address _owner
    ) public virtual override authorised(node) returns (bytes32) {
        bytes32 subnode = keccak256(abi.encodePacked(node, label));
        _setOwner(subnode, _owner);
        emit NewOwner(node, label, _owner);
        return subnode;
    }

    function setSubnodeResolver(
        bytes32 node,
        bytes32 label,
        address _resolver
    ) public virtual override authorised(node) returns (bytes32) {
        bytes32 subnode = keccak256(abi.encodePacked(node, label));
        if (_resolver != records[subnode].resolver) {
            records[subnode].resolver = _resolver;
            emit NewResolver(subnode, _resolver);
        }
        return subnode;
    }

    /**
     * @dev Sets the resolver address for the specified node.
     * @param node The node to update.
     * @param _resolver The address of the resolver.
     */
    function setResolver(bytes32 node, address _resolver) public virtual override authorised(node) {
        emit NewResolver(node, _resolver);
        records[node].resolver = _resolver;
    }

    /**
     * @dev Sets the TTL for the specified node.
     * @param node The node to update.
     * @param _ttl The TTL in seconds.
     */
    function setTTL(bytes32 node, uint64 _ttl) public virtual override authorised(node) {
        emit NewTTL(node, _ttl);
        records[node].ttl = _ttl;
    }

    /**
     * @dev Enable or disable approval for a third party ("operator") to manage
     *  all of `msg.sender`'s FNS records. Emits the ApprovalForAll event.
     * @param operator Address to add to the set of authorized operators.
     * @param approved True if the operator is approved, false to revoke approval.
     */
    function setApprovalForAll(address operator, bool approved) external virtual override {
        operators[msg.sender][operator] = approved;
        emit ApprovalForAll(msg.sender, operator, approved);
    }

    /**
     * @dev Returns the address that owns the specified node.
     * @param node The specified node.
     * @return address of the owner.
     */
    function owner(bytes32 node) public view virtual override returns (address) {
        address addr = records[node].owner;
        if (addr == address(this)) {
            return address(0x0);
        }

        return addr;
    }

    /**
     * @dev Returns the address of the resolver for the specified node.
     * @param node The specified node.
     * @return address of the resolver.
     */
    function resolver(bytes32 node) public view virtual override returns (address) {
        return records[node].resolver;
    }

    /**
     * @dev Returns the TTL of a node, and any records associated with it.
     * @param node The specified node.
     * @return ttl of the node.
     */
    function ttl(bytes32 node) public view virtual override returns (uint64) {
        return records[node].ttl;
    }

    /**
     * @dev Returns whether a record has been imported to the registry.
     * @param node The specified node.
     * @return Bool if record exists
     */
    function recordExists(bytes32 node) public view virtual override returns (bool) {
        return records[node].owner != address(0x0);
    }

    /**
     * @dev Query if an address is an authorized operator for another address.
     * @param _owner The address that owns the records.
     * @param operator The address that acts on behalf of the owner.
     * @return True if `operator` is an approved operator for `owner`, false otherwise.
     */
    function isApprovedForAll(address _owner, address operator) external view virtual override returns (bool) {
        return operators[_owner][operator];
    }

    function _setOwner(bytes32 node, address _owner) internal virtual {
        records[node].owner = _owner;
    }

    function _setResolverAndTTL(
        bytes32 node,
        address _resolver,
        uint64 _ttl
    ) internal {
        if (_resolver != records[node].resolver) {
            records[node].resolver = _resolver;
            emit NewResolver(node, _resolver);
        }

        if (_ttl != records[node].ttl) {
            records[node].ttl = _ttl;
            emit NewTTL(node, _ttl);
        }
    }
}

contracts/interfaces/FNS.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

interface FNS {
    // Logged when the owner of a node assigns a new owner to a subnode.
    event NewOwner(bytes32 indexed node, bytes32 indexed label, address owner);

    // Logged when the owner of a node transfers ownership to a new account.
    event Transfer(bytes32 indexed node, address owner);

    // Logged when the resolver for a node changes.
    event NewResolver(bytes32 indexed node, address resolver);

    // Logged when the TTL of a node changes
    event NewTTL(bytes32 indexed node, uint64 ttl);

    // Logged when an operator is added or removed.
    event ApprovalForAll(address indexed owner, address indexed operator, bool approved);

    function setRecord(
        bytes32 node,
        address owner,
        address resolver,
        uint64 ttl
    ) external;

    function setSubnodeRecord(
        bytes32 node,
        bytes32 label,
        address owner,
        address resolver,
        uint64 ttl
    ) external;

    function setSubnodeOwner(
        bytes32 node,
        bytes32 label,
        address owner
    ) external returns (bytes32);

    function setSubnodeResolver(
        bytes32 node,
        bytes32 label,
        address resolver
    ) external returns (bytes32);

    function setResolver(bytes32 node, address resolver) external;

    function setOwner(bytes32 node, address owner) external;

    function setTTL(bytes32 node, uint64 ttl) external;

    function setApprovalForAll(address operator, bool approved) external;

    function owner(bytes32 node) external view returns (address);

    function resolver(bytes32 node) external view returns (address);

    function ttl(bytes32 node) external view returns (uint64);

    function recordExists(bytes32 node) external view returns (bool);

    function isApprovedForAll(address owner, address operator) external view returns (bool);
}

