//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

library StringUtils {
    /**
     * @dev Returns the length of a given string
     *
     * @param s The string to measure the length of
     * @return The length of the input string
     */
    function strlen(string memory s) internal pure returns (uint256) {
        uint256 len;
        uint256 i = 0;
        uint256 bytelength = bytes(s).length;
        for (len = 0; i < bytelength; len++) {
            bytes1 b = bytes(s)[i];
            if (b < 0x80) {
                i += 1;
            } else if (b < 0xE0) {
                i += 2;
            } else if (b < 0xF0) {
                i += 3;
            } else if (b < 0xF8) {
                i += 4;
            } else if (b < 0xFC) {
                i += 5;
            } else {
                i += 6;
            }
        }
        return len;
    }
}

contracts/Multicallable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/IMulticallable.sol";
import "./libs/ERC165.sol";

abstract contract Multicallable is IMulticallable, ERC165 {
    function _multicall(bytes32 nodehash, bytes[] calldata data) internal returns (bytes[] memory results) {
        results = new bytes[](data.length);
        for (uint256 i = 0; i < data.length; i++) {
            if (nodehash != bytes32(0)) {
                bytes32 txNamehash = bytes32(data[i][4:36]);
                require(txNamehash == nodehash, "multicall: All records must have a matching namehash");
            }
            (bool success, bytes memory result) = address(this).delegatecall(data[i]);
            require(success);
            results[i] = result;
        }
        return results;
    }

    // This function provides an extra security check when called
    // from priviledged contracts (such as RegistrarController)
    // that can set records on behalf of the node owners
    function multicallWithNodeCheck(bytes32 nodehash, bytes[] calldata data) external returns (bytes[] memory results) {
        return _multicall(nodehash, data);
    }

    function multicall(bytes[] calldata data) public override returns (bytes[] memory results) {
        return _multicall(bytes32(0), data);
    }

    function supportsInterface(bytes4 interfaceID) public view virtual override returns (bool) {
        return interfaceID == type(IMulticallable).interfaceId || super.supportsInterface(interfaceID);
    }
}

contracts/PublicResolver.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/FNS.sol";
import "./resolvers/AddressResolver.sol";
import "./resolvers/ContentHashResolver.sol";
import "./resolvers/NameResolver.sol";
import "./resolvers/TextResolver.sol";
import "./Multicallable.sol";

/**
 * A simple resolver anyone can use; only allows the owner of a node to set its
 * address.
 */
contract PublicResolver is Multicallable, AddressResolver, ContentHashResolver, NameResolver, TextResolver {
    FNS immutable fns;
    address immutable trustedRegistrarController;
    address immutable trustedReverseRegistrar;

    /**
     * A mapping of operators. An address that is authorised for an address
     * may make any changes to the name that the owner could, but may not update
     * the set of authorisations.
     * (owner, operator) => approved
     */
    mapping(address => mapping(address => bool)) private _operatorApprovals;

    // Logged when an operator is added or removed.
    event ApprovalForAll(address indexed owner, address indexed operator, bool approved);

    constructor(
        FNS _fns,
        address _trustedRegistrarController,
        address _trustedReverseRegistrar
    ) {
        fns = _fns;
        trustedRegistrarController = _trustedRegistrarController;
        trustedReverseRegistrar = _trustedReverseRegistrar;
    }

    /**
     * @dev See {IERC1155-setApprovalForAll}.
     */
    function setApprovalForAll(address operator, bool approved) external {
        require(msg.sender != operator, "ERC1155: setting approval status for self");

        _operatorApprovals[msg.sender][operator] = approved;
        emit ApprovalForAll(msg.sender, operator, approved);
    }

    /**
     * @dev See {IERC1155-isApprovedForAll}.
     */
    function isApprovedForAll(address account, address operator) public view returns (bool) {
        return _operatorApprovals[account][operator];
    }

    function isAuthorised(bytes32 node) internal view override returns (bool) {
        if (msg.sender == trustedRegistrarController || msg.sender == trustedReverseRegistrar) {
            return true;
        }
        address owner = fns.owner(node);
        return owner == msg.sender || isApprovedForAll(owner, msg.sender);
    }

    function supportsInterface(bytes4 interfaceID)
        public
        view
        override(Multicallable, AddressResolver, ContentHashResolver, NameResolver, TextResolver)
        returns (bool)
    {
        return super.supportsInterface(interfaceID);
    }
}

contracts/Registrar.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/FNS.sol";
import "./interfaces/IRegistrar.sol";
import "./libs/ERC721.sol";
import "./libs/ERC721Enumerable.sol";
import "./libs/Ownable.sol";

contract Registrar is IRegistrar, ERC721Enumerable, Ownable {
    // A map of expiry times
    mapping(uint256 => uint256) public expiries;
    // The FNS registry
    FNS public fns;
    // The namehash of the TLD this registrar owns (eg, .fil)
    bytes32 public baseNode;
    // A map of addresses that are authorised to register and renew names.
    mapping(address => bool) public controllers;
    uint256 public constant GRACE_PERIOD = 90 days;

    mapping(uint256 => string) private names;

    string _baseUri;

    constructor(FNS _fns, bytes32 _baseNode) ERC721("FNS", "FNS") {
        fns = _fns;
        baseNode = _baseNode;
    }

    modifier live() {
        require(fns.owner(baseNode) == address(this));
        _;
    }

    modifier onlyController() {
        require(controllers[msg.sender]);
        _;
    }

    /**
     * v2.1.3 version of _isApprovedOrOwner which calls ownerOf(tokenId) and takes grace period into consideration instead of ERC721.ownerOf(tokenId);
     * https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v2.1.3/contracts/token/ERC721/ERC721.sol#L187
     * @dev Returns whether the given spender can transfer a given token ID
     * @param spender address of the spender to query
     * @param tokenId uint256 ID of the token to be transferred
     * @return bool whether the msg.sender is approved for the given token ID,
     *    is an operator of the owner, or is the owner of the token
     */
    function _isApprovedOrOwner(address spender, uint256 tokenId) internal view override returns (bool) {
        address owner = ownerOf(tokenId);
        return (spender == owner || getApproved(tokenId) == spender || isApprovedForAll(owner, spender));
    }

    /**
     * @dev Gets the owner of the specified token ID. Names become unowned
     *      when their registration expires.
     * @param tokenId uint256 ID of the token to query the owner of
     * @return address currently marked as the owner of the given token ID
     */
    function ownerOf(uint256 tokenId) public view override(IERC721, ERC721) returns (address) {
        require(expiries[tokenId] > block.timestamp);
        return super.ownerOf(tokenId);
    }

    // Authorises a controller, who can register and renew domains.
    function addController(address controller) external onlyOwner {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    // Revoke controller permission for an address.
    function removeController(address controller) external onlyOwner {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }

    // Set the resolver for the TLD this registrar manages.
    function setResolver(address resolver) external onlyOwner {
        fns.setResolver(baseNode, resolver);
    }

    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseUri = baseURI;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseUri;
    }

    // Returns the expiration timestamp of the specified id.
    function nameExpires(uint256 id) external view override returns (uint256) {
        return expiries[id];
    }

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) public view override returns (bool) {
        // Not available if it's registered here or in its grace period.
        return expiries[id] + GRACE_PERIOD < block.timestamp;
    }

    // Get the name of a registered id
    function name(uint256 id) external view returns (string memory) {
        return names[id];
    }

    /**
     * @dev Register a name.
     * @param _name The name to registry
     * @param _owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function register(
        string calldata _name,
        address _owner,
        uint256 duration,
        address resolver
    ) external override live onlyController returns (uint256) {
        uint256 expireTime = _register(_name, _owner, duration, true, resolver);
        return expireTime;
    }

    /**
     * @dev Register a name, without modifying the registry.
     * @param _name The name to registry
     * @param _owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function registerOnly(
        string calldata _name,
        address _owner,
        uint256 duration
    ) external live onlyController returns (uint256) {
        return _register(_name, _owner, duration, false, address(0));
    }

    function _register(
        string memory _name,
        address _owner,
        uint256 duration,
        bool updateRegistry,
        address resolver
    ) internal returns (uint256) {
        uint256 id = uint256(keccak256(bytes(_name)));
        require(available(id), "not available");
        // Prevent future overflow
        require(block.timestamp + duration + GRACE_PERIOD > block.timestamp + GRACE_PERIOD, "grace period");

        expiries[id] = block.timestamp + duration;
        if (_exists(id)) {
            // Name was previously owned, and expired
            _burn(id);
        }
        names[id] = _name;
        _mint(_owner, id);
        if (updateRegistry) {
            fns.setSubnodeOwner(baseNode, bytes32(id), _owner);
            if (resolver != address(0)) {
                fns.setSubnodeResolver(baseNode, bytes32(id), resolver);
            }
        }

        emit NameRegistered(id, _owner, block.timestamp + duration);

        return block.timestamp + duration;
    }

    function renew(uint256 id, uint256 duration) external override live onlyController returns (uint256) {
        // Name must be registered here or in grace period
        require(expiries[id] + GRACE_PERIOD >= block.timestamp, "grace period");
        // Prevent future overflow
        require(expiries[id] + duration + GRACE_PERIOD > duration + GRACE_PERIOD, "future overflow");

        expiries[id] += duration;
        emit NameRenewed(id, expiries[id]);
        return expiries[id];
    }

    /**
     * @dev Reclaim ownership of a name in FNS, if you own it in the registrar.
     */
    function reclaim(uint256 id, address _owner) external override live {
        require(_isApprovedOrOwner(msg.sender, id), "forbidden");
        fns.setSubnodeOwner(baseNode, bytes32(id), _owner);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721Enumerable, IERC165) returns (bool) {
        return interfaceId == type(IRegistrar).interfaceId || super.supportsInterface(interfaceId);
    }
}

contracts/RegistrarController.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./Registrar.sol";
import "./interfaces/IPriceOracle.sol";
import "./interfaces/IRegistrarController.sol";
import "./interfaces/IResolver.sol";
import "./interfaces/IReverseRegistrar.sol";
import "./libs/Address.sol";
import "./libs/IERC165.sol";
import "./libs/Ownable.sol";
import "./libs/SafeERC20.sol";
import "./libs/StringUtils.sol";

error NameNotAvailable(string name);
error DurationTooShort(uint256 duration);
error ResolverRequiredWhenDataSupplied();
error InsufficientValue();
error Unauthorised(bytes32 node);

/**
 * @dev A registrar controller for registering and renewing names at fixed cost.
 */
contract RegistrarController is IRegistrarController, Ownable, IERC165 {
    using StringUtils for *;
    using Address for address;
    using SafeERC20 for IERC20;

    uint256 public constant MIN_REGISTRATION_DURATION = 28 days;
    // namehash("fil")
    bytes32 private constant FIL_NODE = 0x78f6b1389af563cc5c91f234ea46b055e49658d8b999eeb9e0baef7dbbc93fdb;
    uint64 private constant MAX_EXPIRY = type(uint64).max;
    IRegistrar public immutable base;
    IReverseRegistrar public immutable reverseRegistrar;
    IPriceOracle public prices;

    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint256 baseCost,
        uint256 premium,
        uint256 expires
    );
    event NameRenewed(string name, bytes32 indexed label, uint256 cost, uint256 expires);

    constructor(
        IRegistrar _base,
        IPriceOracle _prices,
        IReverseRegistrar _reverseRegistrar
    ) {
        base = _base;
        prices = _prices;
        reverseRegistrar = _reverseRegistrar;
    }

    function valid(string memory name) public pure override returns (bool) {
        return name.strlen() >= 3;
    }

    function available(string memory name) public view override returns (bool) {
        bytes32 label = keccak256(bytes(name));
        return valid(name) && base.available(uint256(label));
    }

    function nameExpires(string memory name) external view override returns (uint256) {
        bytes32 label = keccak256(bytes(name));
        return base.nameExpires(uint256(label));
    }

    function rentPrice(string memory name, uint256 duration)
        public
        view
        override
        returns (IPriceOracle.Price memory price)
    {
        bytes32 label = keccak256(bytes(name));
        price = prices.price(name, base.nameExpires(uint256(label)), duration);
    }

    function register(
        string calldata name,
        address _owner,
        uint256 duration,
        address resolver,
        bytes[] calldata data,
        bool reverseRecord
    ) public payable override {
        if (!available(name)) {
            revert NameNotAvailable(name);
        }
        if (duration < MIN_REGISTRATION_DURATION) {
            revert DurationTooShort(duration);
        }
        if (data.length > 0 && resolver == address(0)) {
            revert ResolverRequiredWhenDataSupplied();
        }

        IPriceOracle.Price memory price = rentPrice(name, duration);
        uint256 totalPrice = price.base + price.premium;
        if (msg.value < totalPrice) {
            revert InsufficientValue();
        }

        uint256 expires = base.register(name, _owner, duration, resolver);

        bytes32 label = keccak256(bytes(name));
        if (data.length > 0) {
            _setRecords(resolver, label, data);
        }

        if (reverseRecord) {
            _setReverseRecord(name, resolver, msg.sender);
        }

        emit NameRegistered(name, label, _owner, price.base, price.premium, expires);

        if (msg.value > totalPrice) {
            payable(msg.sender).transfer(msg.value - totalPrice);
        }
    }

    function renew(string calldata name, uint256 duration) external payable override {
        IPriceOracle.Price memory price = rentPrice(name, duration);
        if (msg.value < price.base) {
            revert InsufficientValue();
        }
        uint256 expires;
        bytes32 label = keccak256(bytes(name));
        expires = base.renew(uint256(label), duration);

        if (msg.value > price.base) {
            payable(msg.sender).transfer(msg.value - price.base);
        }

        emit NameRenewed(name, label, msg.value, expires);
    }

    function setPriceOracle(IPriceOracle _prices) external onlyOwner {
        prices = _prices;
    }

    function withdraw() external {
        payable(owner()).transfer(address(this).balance);
    }

    function withdrawERC20(
        IERC20 token,
        address to,
        uint256 amount
    ) external onlyOwner {
        token.safeTransfer(to, amount);
    }

    function supportsInterface(bytes4 interfaceID) external pure returns (bool) {
        return interfaceID == type(IERC165).interfaceId || interfaceID == type(IRegistrarController).interfaceId;
    }

    /* Internal functions */

    function _setRecords(
        address resolverAddress,
        bytes32 label,
        bytes[] calldata data
    ) internal {
        bytes32 nodehash = keccak256(abi.encodePacked(FIL_NODE, label));
        IResolver resolver = IResolver(resolverAddress);
        resolver.multicallWithNodeCheck(nodehash, data);
    }

    function _setReverseRecord(
        string memory name,
        address resolver,
        address _owner
    ) internal {
        reverseRegistrar.setNameForAddr(msg.sender, _owner, resolver, string.concat(name, ".fil"));
    }
}

contracts/ResolverBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./libs/ERC165.sol";
import "./resolvers/IVersionableResolver.sol";

abstract contract ResolverBase is ERC165, IVersionableResolver {
    mapping(bytes32 => uint64) public recordVersions;

    function isAuthorised(bytes32 node) internal view virtual returns (bool);

    modifier authorised(bytes32 node) {
        require(isAuthorised(node));
        _;
    }

    /**
     * Increments the record version associated with an FNS node.
     * May only be called by the owner of that node in the FNS registry.
     * @param node The node to update.
     */
    function clearRecords(bytes32 node) public virtual authorised(node) {
        recordVersions[node]++;
        emit VersionChanged(node, recordVersions[node]);
    }

    function supportsInterface(bytes4 interfaceID) public view virtual override returns (bool) {
        return interfaceID == type(IVersionableResolver).interfaceId || super.supportsInterface(interfaceID);
    }
}

contracts/resolvers/AddressResolver.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "../ResolverBase.sol";
import "./IAddressResolver.sol";

abstract contract AddressResolver is IAddressResolver, ResolverBase {
    // https://github.com/satoshilabs/slips/blob/master/slip-0044.md
    uint256 private constant COIN_TYPE_FIL = 461;

    mapping(uint64 => mapping(bytes32 => mapping(uint256 => bytes))) versionable_addresses;

    /**
     * Sets the address associated with an FNS node.
     * May only be called by the owner of that node in the FNS registry.
     * @param node The node to update.
     * @param a The address to set.
     */
    function setAddr(bytes32 node, address a) external virtual authorised(node) {
        setAddr(node, COIN_TYPE_FIL, addressToBytes(a));
    }

    /**
     * Returns the address associated with an FNS node.
     * @param node The FNS node to query.
     * @return The associated address.
     */
    function addr(bytes32 node) public view virtual returns (address payable) {
        bytes memory a = addr(node, COIN_TYPE_FIL);
        if (a.length == 0) {
            return payable(0);
        }
        return bytesToAddress(a);
    }

    function setAddr(
        bytes32 node,
        uint256 coinType,
        bytes memory a
    ) public virtual authorised(node) {
        emit AddressChanged(node, coinType, a);
        versionable_addresses[recordVersions[node]][node][coinType] = a;
    }

    function addr(bytes32 node, uint256 coinType) public view virtual override returns (bytes memory) {
        return versionable_addresses[recordVersions[node]][node][coinType];
    }

    function supportsInterface(bytes4 interfaceID) public view virtual override returns (bool) {
        return interfaceID == type(IAddressResolver).interfaceId || super.supportsInterface(interfaceID);
    }

    function bytesToAddress(bytes memory b) internal pure returns (address payable a) {
        require(b.length == 20);
        assembly {
            a := div(mload(add(b, 32)), exp(256, 12))
        }
    }

    function addressToBytes(address a) internal pure returns (bytes memory b) {
        b = new bytes(20);
        assembly {
            mstore(add(b, 32), mul(a, exp(256, 12)))
        }
    }
}

